glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};

in vec3 a_position;

#if USE_TEXTURE
in vec2 a_uv0;
out vec2 uv0;
#endif

vec4 vert () {
  vec4 pos = vec4(a_position, 1);

  #if USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif

  #if USE_TEXTURE
  uv0 = a_uv0;
  #endif

  return pos;
}

void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;

#if USE_ALPHA_TEST

  uniform ALPHA_TEST {
    float alphaThreshold;
  }
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

#if USE_TEXTURE
in vec2 uv0;
uniform sampler2D texture;
#endif

vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);

  #if USE_TEXTURE
  o *= texture(texture, uv0);
    #if _USE_ETC1_TEXTURE
    o.a *= texture2D(texture, uv0 + vec2(0, 0.5)).r;
    #endif
  #endif

  ALPHA_TEST(o);

  return o;
}

out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

